//import React, { useState } from "react";
import Button from "../../UI/Button/Button";
import "./Warning.css"
import styled from "styled-components";

//added the display editing here 
const ErrorMessage = styled.div`
  margin: 0.5rem 0;

  & .shade {
    display: ${(props) => (props.nameError && props.ageError ? "block" : "none")};; 
    position: absolute;
    top: 0%;
    left: 0%;
    width: 100%;
    height: 100%;
    background-color: black;
    z-index:1001;
    -moz-opacity: 0.8;
    opacity:.80;
    filter: alpha(opacity=80);
  }

  & span {
    font-weight: bold;
    display: none;
    margin-bottom: 0.5rem;
  }
`;

const WarningMessage = props => {
   //let popupClasses = "popup"
   //const popupClassChanger = () => {}
   //const removeError = () => {
   //props.onConfirmError;}

    return (
        <ErrorMessage>
            <div className="shade">
            </div>
            <span>
                <h2>Invalid input</h2>
                <Button type="button" onClick={props.onConfirmError}></Button>
            </span>    
        </ErrorMessage>
    );
};

export default WarningMessage;
